import Shared

public func day6() {
//    print(part1(input: input))
    print(part2(input: input))
}

private func part1(input: String) -> Int {
    let characters = input.map { $0 }
    for i in 3..<characters.count {
        let lastFour = characters.dropFirst(i-3).prefix(4)
        if Set(lastFour).count == 4 {
            return i + 1
        }
    }
    return -1
}

private func part2(input: String) -> Int {
    let characters = input.map { $0 }
    for i in 13..<characters.count {
        let lastFour = characters.dropFirst(i-13).prefix(14)
        if Set(lastFour).count == 14 {
            return i + 1
        }
    }
    return 0
}

